DWORD updateCRC32(unsigned char ch, DWORD crc)
{
    return (crc_32_tab[((crc) ^ ((BYTE) ch)) & 0xff] ^ ((crc) >> 8));
}

Boolean_T crc32file(char *name, DWORD * crc, long *charcnt)
{
    register FILE *fin;
    register DWORD oldcrc32;
    register int c;
    oldcrc32 = 0xFFFFFFFF;
    *charcnt = 0;
    if ((fin = fopen(name, "r")) == ((void *) 0)) {
	perror(name);
	return Error_;
    }
    while ((c = _IO_getc(fin)) != (-1)) {
	++*charcnt;
	oldcrc32 = (crc_32_tab[((oldcrc32) ^ ((BYTE) c)) & 0xff] ^ ((oldcrc32) >> 8));
    }
    if (ferror(fin)) {
	perror(name);
	*charcnt = -1;
    }
    fclose(fin);
    *crc = oldcrc32 = ~oldcrc32;
    return Success_;
}

DWORD crc32buf(char *buf, size_t len)
{
    register DWORD oldcrc32;
    oldcrc32 = 0xFFFFFFFF;
    for (; len; --len, ++buf) {
	oldcrc32 = (crc_32_tab[((oldcrc32) ^ ((BYTE) * buf)) & 0xff] ^ ((oldcrc32) >> 8));
    }
    return ~oldcrc32;
}

int main1(int argc, char *argv[], int print)
{
    DWORD crc;
    long charcnt;
    register int errors = 0;
    while (--argc > 0) {
	errors |= crc32file(*++argv, &crc, &charcnt);
	if (print)
	    printf("%08lX %7ld %s\n", crc, charcnt, *argv);
    }
    return (errors != 0);
}
